import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';

// redux dependencies
import { createStore, combineReducers } from 'redux'
import { Provider } from 'react-redux'


let chipInitialState = {
  // Change the key-value pairs here
  chips: []
}

// whatever gets returned from the reducer BECOMES the global state
let chipReducer = (state = chipInitialState, action) => {

  switch (action.type) {

    case "ADD_ONE_CHIP":

      let theChipIwantToAdd = action.payload
      let copyOfChips = [...state.chips, theChipIwantToAdd]


      return {
        ...state,
        chips: copyOfChips
      }


    case "REMOVE_CHIP":
      let theChipName = action.payload
      let filteredChips = state.chips.filter((chip) => {
        return chip.name !== theChipName
      })

      return {
        ...state,
        chips: filteredChips
      }

    default:
      return state
  }

}









let userInitialState = {
  id: 0,
  token: "",
  username: "",
  user_chips: [],
}

let userReducer = (state = userInitialState, action) => {

  switch (action.type) {
    default:
      return state
  }

}



let rootReducer = combineReducers({
  chipInformation: chipReducer,
  userInformation: userReducer,
  // any other reducers will go here
})





let storeObject = createStore(rootReducer, window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__())








ReactDOM.render(
    <Provider store={storeObject}>
      <App />
    </Provider>,
  document.getElementById('root')
);
































